import React from 'react';
import { Text } from './components/Text';
import { Radio } from './components/Radio';
import { Checkbox } from './components/Checkbox';
import { Page } from './Page';

export const Survey = () => {
  const f = [
    { type: 'text', questionid: 1, question: "What's your name" },
    {
      type: 'radio',
      questionid: 2,
      question: "What's your sex",
      options: [
        { id: 1, name: 'female' },
        { id: 2, name: 'male' },
      ],
    },
    {
      type: 'checkbox',
      questionid: 3,
      question: "What's your hobbies",
      options: [
        { id: 1, name: 'singing' },
        { id: 2, name: 'dancing' },
      ],
    },
    { type: 'date', questionid: 4, question: 'Date of birth' },
  ];
  const answers = {
    answerid: null,
    questionid: null,
    answer: null,
    answerOptionId: null,
  };
  const arr = [];

  const handleOnChange = (id, field, value) => {
    const index = arr.findIndex((key) => key.questionid === id);
    arr[index] = { ...arr[index], [field]: value };
  };

  const onSubmit = () => {
    console.log(arr);
  };

  const getValue = (id) => {
    const index = arr.findIndex((key) => key.questionid === id);
    return arr[index];
  };

  const fields = (key) => {
    arr.push({ ...answers, questionid: key.questionid });
    switch (key.type) {
      case 'text':
        return (
          <Text
            label={key.question}
            value={getValue(key.questionid)}
            handleOnChange={handleOnChange}
          />
        );
      case 'radio':
        return (
          <Radio
            label={key.question}
            value={getValue(key.questionid)}
            handleOnChange={handleOnChange}
            options={key.options}
          />
        );
      case 'checkbox':
        return (
          <Checkbox
            label={key.question}
            value={getValue(key.questionid)}
            handleOnChange={handleOnChange}
            options={key.options}
          />
        );
      default:
        return null;
    }
  };
  return (
    <Page>
      <div className=''>
        {f.map((key, index) => (
          <div key={index}>{fields(key)}</div>
        ))}
        <button className='btn btn-primary' onClick={onSubmit}>
          submit
        </button>
      </div>
    </Page>
  );
};
