import React, { useState, useEffect } from 'react';
import { Text } from './components/Text';
import { Radio } from './components/Radio';
import { Checkbox } from './components/Checkbox';
import { Page } from './Page';
import { apiRequest } from '../utils/apiRequest';
import { useParams } from 'react-router-dom';

export const Survey = () => {
  const { id } = useParams();
  const [form, setForm] = useState({});
  const [questions, setQuestions] = useState({});
  const [questionsOptions, setQuestionsOptions] = useState([]);

  const fetchData = async () => {
    const form = await apiRequest
      .get(`/form.php?id=${id}`)
      .then((res) => {
        return res.data[0] || {};
      })
      .catch((error) => {
        console.log(error);
      });

    const questions = await apiRequest
      .get(`/question.php?isList=true&id=${form.id}`)
      .then((res) => {
        console.log(res.data);
      })
      .catch((error) => {
        console.log(error);
      });

    const options = await apiRequest
      .get('/question_options.php')
      .then((res) => {
        console.log(res.data);
      })
      .catch((error) => {
        console.log(error);
      });

    setForm(form);
    setQuestions(questions);
    setQuestionsOptions(options);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const f = [
    { type: 'text', id: 1, question: "What's your name" },
    {
      type: 'radio',
      id: 2,
      question: "What's your sex",
      options: [
        { id: 1, name: 'female' },
        { id: 2, name: 'male' },
      ],
    },
    {
      type: 'checkbox',
      id: 3,
      question: "What's your hobbies",
      options: [
        { id: 1, name: 'singing' },
        { id: 2, name: 'dancing' },
      ],
    },
    { type: 'date', id: 4, question: 'Date of birth' },
  ];

  const getOptions = (id) => {};

  const answers = {
    answerid: null,
    questionid: null,
    answer: null,
    answerOptionId: null,
  };
  const arr = [];

  const handleOnChange = (id, field, value) => {
    const index = arr.findIndex((key) => key.questionid === id);
    arr[index] = { ...arr[index], [field]: value };
  };

  const onSubmit = () => {
    console.log(arr);
  };

  const getValue = (id) => {
    const index = arr.findIndex((key) => key.questionid === id);
    return arr[index];
  };

  const fields = (key) => {
    arr.push({ ...answers, questionid: key.id });
    switch (key.type) {
      case 'text':
        return (
          <Text label={key.question} value={getValue(key.id)} handleOnChange={handleOnChange} />
        );
      case 'radio':
        return (
          <Radio
            label={key.question}
            value={getValue(key.id)}
            handleOnChange={handleOnChange}
            options={key.options}
          />
        );
      case 'checkbox':
        return (
          <Checkbox
            label={key.question}
            value={getValue(key.id)}
            handleOnChange={handleOnChange}
            options={key.options}
          />
        );
      default:
        return null;
    }
  };
  return (
    <Page>
      <h4 className='text-center mb-3'>{form.name}</h4>
      {f.map((key, index) => (
        <div key={index}>{fields(key)}</div>
      ))}
      <button className='btn btn-primary' onClick={onSubmit}>
        submit
      </button>
    </Page>
  );
};
