import React from 'react';

export const Radio = ({
  label = 'text',
  value = {
    answerid: null,
    questionid: null,
    answer: null,
    answerOptionId: null,
  },
  options = [],
  handleOnChange,
}) => {
  return (
    // <div className='form-check mb-3'>
    //   <p>{label}</p>
    //   <input type='radio' class='form-check-input' name='optradio' value={value.answerOptionId} />
    //   Option 1<label class='form-check-label' for='radio1'></label>
    // </div>

    <div class='form-check form-check-inline'>
      <p>{label}</p>
      {options.map((key, index) => (
        <React.Fragment className='mx-3' key={index}>
          <input
            class='form-check-input'
            type='radio'
            name='optradio'
            value={value.answerOptionId}
          />
          <label class='form-check-label'>{key}</label>
        </React.Fragment>
      ))}
    </div>
  );
};
