import React, { useState, useEffect } from 'react';
import { Page } from './Page';
import { apiRequest } from '../utils/apiRequest';
import { useParams } from 'react-router-dom';

export const FormDetail = () => {
  const { id } = useParams();
  const [form, setForm] = useState({});
  const [questions, setQuestions] = useState({});
  const [questionsOptions, setQuestionsOptions] = useState([]);

  const fetchData = async () => {
    const form = await apiRequest
      .get(`/form.php?id=${id}`)
      .then((res) => {
        return res.data[0] || {};
      })
      .catch((error) => {
        console.log(error);
      });

    const questions = await apiRequest
      .get(`/question.php?isList=true&id=${form.id}`)
      .then((res) => {
        return res.data;
      })
      .catch((error) => {
        console.log(error);
      });

    const options = await apiRequest
      .get('/question_options.php')
      .then((res) => {
        return res.data;
      })
      .catch((error) => {
        console.log(error);
      });

    setForm(form);
    setQuestions(questions);
    setQuestionsOptions(options);
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <Page>
      <h4 className='text-center mb-3'>{form.name}</h4>
      {questions.map((question) => (
        <div className='card mb-3' key={question.id}>
          <div className='card-header'>{question.name}</div>
          <div className='px-3 py-2'>Type: {question.type}</div>
          <div className='px-3 py-2'>Is Required: {question.isRequired ? 'true' : 'false'}</div>
          <div cclassName='px-3 py-2'>Is Filter: {question.isFilter}</div>
        </div>
      ))}
    </Page>
  );
};
