import React from 'react';

export const Checkbox = ({
  label = 'text',
  value = {
    answerid: null,
    questionid: null,
    answer: null,
    answerOptionId: null,
  },
  options = [],
  handleOnChange,
}) => {
  const getIds = (id) => {
    const arr = [];
    const index = arr.findIndex((arr) => arr === id);
    if (index > -1) {
      arr.splice(index, 1);
    } else {
      arr.push(id);
    }
    handleOnChange(value.questionid, 'answerOptionId', arr);
  };
  return (
    <div className='mb-3 mx-4'>
      <h6>{label}</h6>
      {options.map((key, index) => (
        <div class='form-check form-check-inline' key={index}>
          <input
            className='form-check-input'
            type='checkbox'
            name={label}
            value={key.id}
            onChange={(e) => getIds(target.value)}
          />
          <label class='form-check-label'>{key.name}</label>
        </div>
      ))}
    </div>
  );
};
